sections.code:
.top-nav,
.side-nav,
.chat-input,
.channel-leaderboard,
.stream-chat-header,
.community-highlight-stack__card,
.report-button,
[data-a-target="share-button"],
div[aria-label="Recommended Channels"],
div[data-target="channel-header-right"] {
    display: none !important;
}

div[data-test-selector="right-column-content__toggle"] {
    background-color: var(--color-twitch-purple-8);
    border-radius: 8px;
    box-shadow: 0 3px 9px rgba(0, 0, 0, 0.75);
}

.channel-info-content {
    opacity: 0;
    transition: opacity 0.5s ease-in-out;
}

.channel-info-content:hover {
    opacity: 1;
}

.channel-panels,
.social-media-space {
    opacity: 0;
    transition: opacity 0.5s ease-in-out;
}

.channel-panels:hover,
.social-media-space:hover {
    opacity: 1;
}

.chat-shell {
    opacity: 0.35;
    filter: grayscale(100%);
    transition: opacity 0.5s ease-in-out;
}

.chat-shell:hover {
    opacity: 1;
}

